(55, 'MŨ LƯỠI TRAI TREFOIL KHUY BẤM', 'mu-luoi-trai-trefoil-khuy-bam', 'uploads/product/1591897253_Mu_luoi_trai_Trefoil_khuy_bam_Mau_djen_DV0176_01_standard.jpg', 8, 600000, 599000, 20, 1, 1, 0, 17, 'adidas.com', 'MUADIDAS1', 7, 6, 'MŨ LƯỠI TRAI TREFOIL KHUY BẤM\r\nMẪU MŨ LƯỠI TRAI VÀNH THẲNG VỚI NÉT ĐẶC TRƯNG KHÔNG THỂ NHẦM LẪN CỦA ADIDAS.', '<p>MŨ LƯỠI TRAI TREFOIL KHUY BẤM</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"/ckfinder/userfiles/images/Product/Product5/Mu_luoi_trai_Trefoil_khuy_bam_Mau_djen_DV0176_02_standard.jpg\" width=\"500\" /></p>\r\n\r\n<p>MẪU MŨ LƯỠI TRAI V&Agrave;NH THẲNG VỚI N&Eacute;T ĐẶC TRƯNG KH&Ocirc;NG THỂ NHẦM LẪN CỦA ADIDAS.</p>\r\n\r\n<p>Thể hiện niềm tự h&agrave;o adidas của bạn. Mẫu mũ lưỡi trai v&agrave;nh thẳng n&agrave;y l&agrave;m nổi bật phong c&aacute;ch adidas Originals đ&iacute;ch thực với logo Trefoil lớn th&ecirc;u ở đằng trước. Mũ được thiết kế để tạo ra sự thoải m&aacute;i, với chất liệu cotton co gi&atilde;n v&agrave; khuy bấm điều chỉnh được.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"/ckfinder/userfiles/images/Product/Product5/Mu_luoi_trai_Trefoil_khuy_bam_Mau_djen_DV0176_42_detail.jpg\" width=\"500\" /></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, '2020-06-11 10:40:53', '2020-07-09 08:49:04'),
(56, 'Giày Thể Thao Adidas Falcon Shoes Women\'s Màu Hồng Tím', 'giay-the-thao-adidas-falcon-shoes-womens-mau-hong-tim', 'uploads/product/1594307833_giay-the-thao-adidas-falcon-shoes-women-s-mau-hong-tim-5e746775750bf-20032020134925.jpg', 11, 3000000, 2200000, 21, 1, 1, 0, 26, 'adidas.com', 'h022564', 7, 6, 'Kiểu dáng: Giày thể thao\r\nGiới tính: Nữ\r\nMàu sắc: Hồng tím', '<h2>Mua Gi&agrave;y Thể Thao Adidas Falcon Shoes Women&#39;s M&agrave;u Hồng T&iacute;m</h2>\r\n\r\n<p><strong>Gi&agrave;y Thể Thao Adidas Falcon Shoes Women&#39;s M&agrave;u Hồng T&iacute;m&nbsp;</strong>mặt h&agrave;ng&nbsp;<a href=\"https://vuahanghieu.com/giay\">gi&agrave;y d&eacute;p thời trang</a>&nbsp;chất lượng ch&iacute;nh h&atilde;ng của&nbsp;<a href=\"https://vuahanghieu.com/adidas\">thương hiệu Adidas&nbsp;</a>nổi tiếng thế giới,&nbsp;&nbsp;Gi&agrave;y Thể Thao Adidas Falcon Shoes Women&#39;s M&agrave;u Hồng T&iacute;m đang được giảm gi&aacute; ưu đ&atilde;i&nbsp; tại gian h&agrave;ng&nbsp;VHH tr&ecirc;n s&agrave;n thương mại điện tử&nbsp;<a href=\"https://vuahanghieu.com/\">Vua H&agrave;ng Hiệu</a>.</p>\r\n\r\n<h3><strong>V&agrave;i n&eacute;t về Gi&agrave;y Thể Thao Adidas Falcon Shoes Women&#39;s M&agrave;u Hồng T&iacute;m</strong></h3>\r\n\r\n<p><strong>Gi&agrave;y Thể Thao Adidas Falcon Shoes Women&#39;s M&agrave;u Hồng T&iacute;m</strong>&nbsp;được thiết kế trẻ trung, hiện đại. Đ&acirc;y l&agrave; một trong những đ&ocirc;i gi&agrave;y Hot Hit hiện nay được đ&ocirc;ng đảo c&aacute;c bạn trẻ y&ecirc;u th&iacute;ch lựa chọn.</p>\r\n\r\n<p>Gi&agrave;y l&agrave;m từ chất liệu da, vải lưới cao cấp mang đến cảm gi&aacute;c thoải m&aacute;i nhất cho người sử dụng. M&agrave;u sắc đơn giản dễ d&agrave;ng kết hợp với c&aacute;c trang phục kh&aacute;c nhau để thay đổi phong c&aacute;ch cho bản th&acirc;n năng động hơn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"https://cdn.vuahanghieu.com/unsafe/0x0/left/top/smart/filters:quality(90)/https://admin.vuahanghieu.com/upload/news/content/2020/03/81786500_476830643020768_610271890203738112_o-jpg-1584687548-20032020135908.jpg\" width=\"500\" /></p>\r\n\r\n<p>Đường may của gi&agrave;y tinh tế, tỉ mỉ từng chi tiết, đảm bảo h&agrave;i l&ograve;ng ngay cả với kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh nhất. Phần đế gi&agrave;y chống trơn trượt an to&agrave;n, sản phẩm bền đẹp trong suốt qu&aacute; tr&igrave;nh sử dụng.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"https://cdn.vuahanghieu.com/unsafe/0x0/left/top/smart/filters:quality(90)/https://admin.vuahanghieu.com/upload/news/content/2020/03/81626183_476830683020764_6498697998917173248_o-jpg-1584687564-20032020135924.jpg\" width=\"500\" /></p>\r\n\r\n<p>Gi&agrave;y ph&ugrave; hợp đi trong nhiều ho&agrave;n cảnh kh&aacute;c nhau đi học, đi chơi, du lịch, chạy bộ...</p>', NULL, NULL, '2020-06-11 10:43:51', '2020-07-09 08:17:13'),
(57, 'Giày Sneaker Adidas 90s Valasion EG2901 Màu Xám Đen Size 36', 'giay-sneaker-adidas-90s-valasion-eg2901-mau-xam-den-size-36', 'uploads/product/1594307708_giay-the-thao-mau-xam-cho-nu.jpg', 8, 3200000, 2100000, 22, 1, 1, 0, 26, 'adidas.com', 'h023583', 7, 6, 'Kiểu dáng: Sneakers\r\nGiới tính: Nữ\r\nMàu sắc: Xám, đen', '<h2>Mua Gi&agrave;y Sneaker Adidas 90s Valasion EG2901 M&agrave;u X&aacute;m Đen Size 36</h2>\r\n\r\n<p><strong>Gi&agrave;y Sneaker Adidas 90s Valasion EG2901 M&agrave;u X&aacute;m Đen Size 36&nbsp;</strong>mặt h&agrave;ng&nbsp;<a href=\"https://vuahanghieu.com/giay\">gi&agrave;y d&eacute;p thời trang</a>&nbsp;chất lượng ch&iacute;nh h&atilde;ng của&nbsp;<a href=\"https://vuahanghieu.com/adidas\">thương hiệu Adidas&nbsp;</a>nổi tiếng thế giới,&nbsp;&nbsp;Gi&agrave;y Sneaker Adidas 90s Valasion EG2901 M&agrave;u X&aacute;m Đen Size 36 đang được giảm gi&aacute; ưu đ&atilde;i&nbsp; tại gian h&agrave;ng&nbsp;Thể Thao Ch&iacute;nh H&atilde;ng tr&ecirc;n s&agrave;n thương mại điện tử&nbsp;<a href=\"https://vuahanghieu.com/\">Vua H&agrave;ng Hiệu</a>.</p>\r\n\r\n<h3><strong>V&agrave;i n&eacute;t về Gi&agrave;y Sneaker Adidas 90s Valasion EG2901 M&agrave;u X&aacute;m Đen Size 36</strong></h3>\r\n\r\n<p><strong><a href=\"https://vuahanghieu.com/giay/sneakers\">Gi&agrave;y Sneaker</a>&nbsp;Adidas 90s Valasion EG2901 M&agrave;u X&aacute;m Đen</strong>&nbsp;thiết kế đẹp mắt, kiểu d&aacute;ng hiện đại. Với đ&ocirc;i gi&agrave;y thời trang n&agrave;y chắc chắn bạn sẽ trở n&ecirc;n năng động v&agrave; trẻ trung hơn.</p>\r\n\r\n<p>Form gi&agrave;y chuẩn đẹp, c&aacute;c đường may rất chắc chắn v&agrave; tinh đế đảm bảo h&agrave;i l&ograve;ng mọi kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Th&acirc;n gi&agrave;y bằng da v&agrave; vải lưới, đế giữa c&ocirc;ng nghệ đệm Cloudfoam v&agrave; l&oacute;t gi&agrave;y OrthoLite, đế ngo&agrave;i bằng chất liệu tổng hợp.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"https://cdn.vuahanghieu.com/unsafe/0x0/left/top/smart/filters:quality(90)/https://admin.vuahanghieu.com/upload/news/content/2020/05/giay-the-thao-mau-xam-cho-nu-jpg-1588931596-08052020165316.jpg\" width=\"500\" /></p>\r\n\r\n<p>M&agrave;u sắc trung t&iacute;nh dễ d&agrave;ng kết hợp với c&aacute;c trang phục kh&aacute;c nhau, gi&agrave;y ph&ugrave; hợp đi trong nhiều ho&agrave;n cảnh: đi học, đi chơi, dạo phố, luyện tập thể dục, thể thao...</p>', NULL, NULL, '2020-06-11 10:46:49', '2020-07-09 08:15:08'),
(58, 'GIÀY GUCCI RHYTON 1:1', 'giay-gucci-rhyton-11', 'uploads/product/1591936668_giay-gucci-rhyton-1-1.jpg', 5, 2700000, 2400000, 1, 1, 1, 0, 26, 'gucci.com', 'GC6', 9, 2, 'Tulo\'s shop cung cấp giày thể thao ADIDAS chuẩn đẹp, chất lượng, giá cả hợp lý, cam kết chất lượng.\r\nTrạng thái : Còn hàng\r\nTình trạng : Hàng mới 100%\r\nGiao hàng & Thanh toán : Giao hàng trên toàn quốc và thanh toán khi nhận hàng', '<h3>GI&Agrave;Y GUCCI RHYTON 1:1</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"450\" src=\"/ckfinder/userfiles/images/Product/giay-gucci-rhyton-1-1-7.jpg\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"450\" src=\"https://giaygiare.vn/upload/images/giay-gucci-rhyton-1-1.jpg\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"450\" src=\"/ckfinder/userfiles/images/Product/giay-gucci-rhyton-1-1-1.jpg\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"461\" src=\"/ckfinder/userfiles/images/Product/giay-gucci-rhyton-1-1-4.jpg\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"338\" src=\"/ckfinder/userfiles/images/Product/giay-gucci-rhyton-1-1-7(1).jpg\" width=\"450\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giày Gucci Rhyton 1:1\" height=\"450\" src=\"https://giaygiare.vn/upload/images/giay-gucci-rhyton-1-1-5.jpg\" width=\"600\" /></p>', NULL, NULL, '2020-06-11 21:37:48', '2020-07-09 06:50:56'),
(59, 'Giày lười công sở SDrolun màu đen mới nhất', 'giay-luoi-cong-so-sdrolun-mau-den-moi-nhat', 'uploads/product/1594307385_giay-luoi-cong-so-sdrolun-mau-den-moi-nhat-ma-gl21817d-giaynam (2).png', 8, 1300000, 1250000, 30, 1, 1, 0, 27, 'gucci.com', 'GL21817D', 9, 2, 'Giày lười công sở SDrolun màu đen mới nhất mã GL21817D vừa cập bến giaynam.com nổi bật với họa tiết đường gân sóng chính giữa trung tâm.', '<p><em>Những đ&ocirc;i gi&agrave;y hiện nay kh&ocirc;ng c&ograve;n đơn giản l&agrave; m&oacute;n đồ gi&uacute;p bảo vệ đ&ocirc;i ch&acirc;n m&agrave; n&oacute; c&ograve;n trở th&agrave;nh m&oacute;n đồ thể hiện thời trang, phong c&aacute;ch v&agrave; c&aacute; t&iacute;nh ri&ecirc;ng của chủ nh&acirc;n đ&ocirc;i gi&agrave;y. Bởi vậy, nhiều kh&aacute;ch h&agrave;ng tỏ ra tỉ mỉ khi chọn gi&agrave;y v&agrave; họ thường bị thu h&uacute;t bởi đ&ocirc;i <strong>gi&agrave;y lười c&ocirc;ng sở</strong> SDrolun m&agrave;u đen mới nhất m&atilde; GL21817D vừa cập bến giaynam.com.</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"500\" src=\"http://giaynam.com/images/upload/giay-luoi-cong-so-sdrolun-mau-den-moi-nhat-ma-gl21817d-giaynam%20(2).png\" width=\"500\" /></p>\r\n\r\n<p><em><a href=\"http://giaynam.com/giay-luoi/\" target=\"\"><strong>Gi&agrave;y lười nam</strong></a> m&atilde; GL21817D nổi bật với họa tiết đường g&acirc;n s&oacute;ng ch&iacute;nh giữa trung t&acirc;m v&agrave; l&agrave; mẫu gi&agrave;y được c&aacute;nh m&agrave;y r&acirc;u rất ưa chuộng bởi nhiều l&iacute; do. Từ thiết kế gi&agrave;y đến chất lượng đế, da gi&agrave;y đều được lựa chọn v&agrave; t&iacute;nh to&aacute;n cẩn thận từ những nguy&ecirc;n liệu tốt nhất. Bởi vậy ch&uacute;ng ta c&oacute; một đ&ocirc;i gi&agrave;y tốt, hoản hảo dễ d&agrave;ng kết hợp với c&aacute;c trang phục : quần &acirc;u/t&acirc;y, kaki, &aacute;o sơ mi, vest... cho những ho&agrave;n cảnh kh&aacute;c nhau. C&aacute;c bạn cũng c&oacute; thể xem th&ecirc;m c&aacute;c mẫu gi&agrave;y <a href=\"http://giayna